// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Scholarship {
    address public admin;
    mapping(address => uint256) public scholarshipBalances;
    mapping(address => bool) public scholarshipRecipients;
    
    struct Application {
        address student;
        string name;
        string department;
        uint256 year;
        string reason;
        uint256 amount;
        bool isApproved;
        bool isRejected;
        uint256 timestamp;
    }
    
    Application[] public applications;
    mapping(address => uint256) public applicationCount;
    
    event ScholarshipGranted(address indexed student, uint256 amount);
    event ScholarshipWithdrawn(address indexed student, uint256 amount);
    event ApplicationSubmitted(address indexed student, uint256 applicationId);
    event ApplicationApproved(address indexed student, uint256 applicationId);
    event ApplicationRejected(address indexed student, uint256 applicationId);
    
    constructor() {
        admin = msg.sender;
    }
    
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }
    
    function submitApplication(
        string memory name,
        string memory department,
        uint256 year,
        string memory reason,
        uint256 amount
    ) public {
        uint256 applicationId = applications.length;
        applications.push(Application({
            student: msg.sender,
            name: name,
            department: department,
            year: year,
            reason: reason,
            amount: amount,
            isApproved: false,
            isRejected: false,
            timestamp: block.timestamp
        }));
        applicationCount[msg.sender]++;
        emit ApplicationSubmitted(msg.sender, applicationId);
    }
    
    function approveApplication(uint256 applicationId) public onlyAdmin {
        require(applicationId < applications.length, "Invalid application ID");
        Application storage application = applications[applicationId];
        require(!application.isApproved && !application.isRejected, "Application already processed");
        
        application.isApproved = true;
        scholarshipBalances[application.student] += application.amount;
        scholarshipRecipients[application.student] = true;
        
        emit ApplicationApproved(application.student, applicationId);
        emit ScholarshipGranted(application.student, application.amount);
    }
    
    function rejectApplication(uint256 applicationId) public onlyAdmin {
        require(applicationId < applications.length, "Invalid application ID");
        Application storage application = applications[applicationId];
        require(!application.isApproved && !application.isRejected, "Application already processed");
        
        application.isRejected = true;
        emit ApplicationRejected(application.student, applicationId);
    }
    
    function getApplication(uint256 applicationId) public view returns (
        address student,
        string memory name,
        string memory department,
        uint256 year,
        string memory reason,
        uint256 amount,
        bool isApproved,
        bool isRejected,
        uint256 timestamp
    ) {
        require(applicationId < applications.length, "Invalid application ID");
        Application memory application = applications[applicationId];
        return (
            application.student,
            application.name,
            application.department,
            application.year,
            application.reason,
            application.amount,
            application.isApproved,
            application.isRejected,
            application.timestamp
        );
    }
    
    function getApplicationsCount() public view returns (uint256) {
        return applications.length;
    }
    
    function withdrawScholarship(uint256 amount) public {
        require(scholarshipRecipients[msg.sender], "Not a scholarship recipient");
        require(scholarshipBalances[msg.sender] >= amount, "Insufficient scholarship balance");
        
        scholarshipBalances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
        emit ScholarshipWithdrawn(msg.sender, amount);
    }
    
    function getScholarshipBalance(address student) public view returns (uint256) {
        return scholarshipBalances[student];
    }
    
    function isScholarshipRecipient(address student) public view returns (bool) {
        return scholarshipRecipients[student];
    }
}
